'''"Procedural programming" vs. "Object-Oriented Programming (OOP)" '''# 1.Procedural programmingstd1 = { 'name': 'Michael', 'score': 98 }std2 = { 'name': 'Bob', 'score': 81 }def print_score(std):    print('%s: %d' % (std['name'], std['score']))# Procedural printprint_score(std1)print_score(std2)# 2.Object-Oriented Programming (OOP)class Student(object):    def __init__(self, name, score):        self.name = name        self.score = score    def print_score(self):        print('%s: %d' % (self.name, self.score))# OOP print: Methodbart = Student('Bart Simpson', 59)lisa = Student('Lisa Simpson', 87)bart.print_score()lisa.print_score()